catalog_script_client.script:
function onSubmit() {
  var ciTable = g_form.getValue('ci_table');
  var firstServiceType = g_form.getValue('first_service_type');
  var firstServiceColumn = g_form.getValue('first_service_column');
  var relationship = g_form.getValue('relationship');
  var secondCiTable = g_form.getValue('second_ci_table');
  var secondServiceType = g_form.getValue('second_service_type');
  var secondServiceColumn = g_form.getValue('second_service_column');

  g_form.clearValue('first_service_column');
  g_form.clearValue('second_service_column');

  var data = {
    ciTable: ciTable,
    firstServiceType: firstServiceType,
    firstServiceColumn: firstServiceColumn,
    relationship: relationship,
    secondCiTable: secondCiTable,
    secondServiceType: secondServiceType,
    secondServiceColumn: secondServiceColumn
  };

  var json = JSON.stringify(data);

  var ga = new GlideAjax('ServiceProvisioningHelper');
  ga.addParam('sysparm_name', 'fillInTable');
  ga.addParam('sysparm_json', json);
  ga.getXML(callback);

  function callback(response) {
    return;
  }

  g_form.addInfoMessage('Dependencies have been added to the "PagerDuty Service Provisioning" table.');

  return false;
}
