sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual) {
	if (!(gs.getProperty('x_pd_integration.use_pd_teams') === 'true' &&
		  gs.getProperty('x_pd_integration.auto_provision_grmembers') === 'true' &&
		  gs.getProperty('x_pd_integration.auto_creation_pd_teams') === 'true')) {
		stepResult.setOutputMessage(
			'You need to set' +
			'"Use PagerDuty Teams"' +
			'"Create PagerDuty Team when provisioning Assignment Group"' +
			'"Provision current Assignment Group members into PagerDuty when provisioning Assignment Groups"' + 
			' to true to run this test'
		);
		return false;
	}
	
	describe('Create team with members', function() {
		it('works', function() {
			var json = new global.JSON();
			var group = new GlideRecord('sys_user_group');
			group.addQuery('name', '=', 'CAB Approval');
			group.setLimit(1);
			group.query();
			group.next();

			var provisionedGroupId = group.getValue('sys_id');

			// START: COPIED FROM UI ACTION "Provision Group into Pagerduty"
			pd = new x_pd_integration.PagerDutyProvisioning();
			pd.provisionGroupService(group);
			// END: COPIED FROM UI ACTION "Provision Group into Pagerduty"

			var provisionedGroup = new GlideRecord('sys_user_group');
			provisionedGroup.get(provisionedGroupId);

			expect(provisionedGroup.getValue('x_pd_integration_pagerduty_escalation'))
				.toEqual(jasmine.any(String));
			expect(provisionedGroup.getValue('x_pd_integration_pagerduty_webhook'))
				.toEqual(jasmine.any(String));
			expect(provisionedGroup.getValue('x_pd_integration_pagerduty_service'))
				.toEqual(jasmine.any(String));
			expect(provisionedGroup.getValue('x_pd_integration_pagerduty_team'))
				.toEqual(jasmine.any(String));

			var groupMember = new GlideRecord('sys_user_grmember');
			groupMember.addQuery('group', provisionedGroupId);
			groupMember.query();

			var apiCalls = json.encode(x_pd_integration.SimpleObjStubUtil.getCalls());
			var teamId = provisionedGroup.getValue('x_pd_integration_pagerduty_team');
			gs.debug(json.encode(apiCalls));

			while (groupMember.next()) {
				var userPDId = provisionedGroup.getValue('x_pd_integration_pagerduty_id');
				var isUserAddedToTeam = false;
				apiCalls.forEach(function(call) {
					if (call.args[0] === '/teams/' + teamId + '/users/' + userPDId) {
						isUserAddedToTeam = true;
					} 
				});

				expect(isUserAddedToTeam).toBe(true);
			}
		});
	});

})(outputs, steps, stepResult, assertEqual);
