sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual) {
	describe('Create team with members', function() {
		it('works', function() {
			var pdp = new PagerDutyProvisioning();
			var group = new GlideRecord('sys_user_group');
			group.addQuery('name', '=', 'CAB Approval');
			group.setLimit(1);
			group.query();
			group.next();
			group.setValue('x_pd_integration_pagerduty_team', null);
			group.update();

			group = new GlideRecord('sys_user_group');
			group.addQuery('name', '=', 'CAB Approval');
			group.setLimit(1);
			group.query();
			group.next();

			var user = new GlideRecord('sys_user');
			user.addQuery('name', '=', 'Allyson Gillispie');
			user.setLimit(1);
			user.query();
			user.next();

			// START: Copied from business rule "PD Add New Group Member to PagerDuty"
			var pdUserId = user.getValue('x_pd_integration_pagerduty_id');
			gs.debug(pdUserId);
			if (!pdUserId) {
				pdUserId = pdp.provisionUser(user);
				gs.addInfoMessage('User provisioned to PD');
			}
			gs.debug('BR WhenUserAddedToGroup | pdUserId: {0}', pdUserId);

			var teamId = group.getValue('x_pd_integration_pagerduty_team');

			gs.debug('BR WhenUserAddedToGroup | teamId: {0}', teamId);

			if (teamId) {
				pdp.addUserToPdTeam(pdUserId, teamId);
				gs.addInfoMessage('User added to team');
			}
			// END: Copied from business rule "PD Add New Group Member to PagerDuty"
			
			expect(!!pdUserId).toBe(true);
		    var apiCalls = x_pd_integration.SimpleObjStubUtil.getCalls();
            var json = new global.JSON();
            gs.debug(json.encode(apiCalls));
            var wasUserAddedToTeam = false;
            for (var i = 0; i < apiCalls.length; i++) {
				if (apiCalls[i].args[0] === 'teams/' + teamId + '/users/' + pdUserId) {
					wasUserAddedToTeam = true;
			    }
		    }
			expect(wasUserAddedToTeam).toBe(false);
		});
	});
})(outputs, steps, stepResult, assertEqual);
