sys_script_client.script:
function onLoad() {
	ScriptLoader.getScripts('x_pd_integration.AjaxHelper.jsdbx?v='+new Date().getTime(), function() {
		var trackingApi = x_pd_integration.AjaxHelper.ajaxClientFor('TrackingController');
		var customFieldsApi = x_pd_integration.AjaxHelper.ajaxClientFor('PagerDutyCustomFieldsController');
		customFieldsApi.getCustomFieldMapping(g_form.getUniqueValue(), function (error, result) {
			if (error) {
				jslog('Error on Client Script for CustomFieldMapping Form with error: ' + error);
			} else if (result.error) {
				// add error message
				g_form.addFormMessage(result.error_reason.replace(/\n/g, "<br />"), 'error');
				// change header position
				var elem = document.getElementById("output_messages");
				jQuery('.form-group').first().append(elem);
				var trackingProps = {
					error_reason: result.error_reason
				};
				trackingApi.track('snow_custom_fields_mapping_detail_load_errors', trackingProps, function (error, _result) {
					if (error) {
						jslog('Tracking error: ' + error);
					}
				});
			}
		});
		// Traking event
		trackingApi.track('custom_fields_mapping_detail_click', {}, function (error, _result) {
			if (error) {
				jslog('Tracking error: ' + error);
			}
		});
	});
}
