sys_script_fix.script:
try {
  gs.info('~~ Started running a PagerDuty Integration Fix Script');
  if (gs.nil(gs.getProperty('x_pd_integration.api_key'))) {
    gs.info('After installation');
  } else {
    gs.info('After upgrade');
    var hc = new x_pd_integration.HealthcheckController();
    var results = hc.check();
  }
} catch (ex) {
  gs.error('[\u2717] Error when running PagerDuty Integration Fix Script: ' +
    global.JSON.stringify(ex, null, 2));
} finally {
  gs.info('~~ Finished running a PagerDuty Integration Fix Script');
}
