sys_script_include.script:
/*** Changes made to this script are not supported by PagerDuty ***/
var PagerDutyProvisionCICondition_DEPRECATED = Class.create();
PagerDutyProvisionCICondition_DEPRECATED.prototype = {
    initialize: function() {
    },

  ProvisionUIAction : function(gr) {
	  var isPdAdmin = gs.hasRole("x_pd_integration.admin");
	  var isServiceNil = gr.x_pd_integration_pagerduty_service.nil();
	  var isWebhookNil = gr.x_pd_integration_pagerduty_webhook.nil();
	  var sn2pd_mapping = gs.getProperty("x_pd_integration.sn2pd_mapping");

  if (isPdAdmin && isServiceNil && isWebhookNil && (sn2pd_mapping == "Configuration Items and Assignment Groups map to PagerDuty"))
  {return true;}
  else
  {return false;}
  },

    type: 'PagerDutyProvisionCICondition_DEPRECATED'
};
