sys_script_include.script:
/*** Changes made to this script are not supported by PagerDuty ***/
var EscalationPolicyHelperController = x_pd_integration.AjaxHelper.createAjaxController({
  type: 'EscalationPolicyHelperController',

  getPdEpId: function convertEpToPd(id) {
    // assume id is snEpId
    var epHelper = new x_pd_integration.EscalationPolicyHelper();
    var pdEpId = epHelper.convertEpToPd(id);
    if (pdEpId)
      return pdEpId;
    // assume id is pdEpId
    var epId = epHelper.convertEpToSn(id);
    if (epId)
      return id;
    return null;
  }
});
