sys_script_include.script:
/*** Changes made to this script are not supported by PagerDuty ***/
var AppManager = {};

Object.defineProperty(AppManager, 'isPdSiIncidentsInstalled', {
  get: function () {
    var grApp = new GlideRecord('sys_app');
    if (grApp.get('scope', 'x_pd_si_incidents')) {
      return true;
    }
    return false;
  },
  configurable: false
});

Object.defineProperty(AppManager, 'isPdChangeRequestsInstalled', {
  get: function () {
    var grApp = new GlideRecord('sys_app');
    if (grApp.get('scope', 'x_pd_changerequest')) {
      return true;
    }
    return false;
  },
  configurable: false
});
