sys_script_include.script:
/*** Changes made to this script are not supported by PagerDuty ***/
var BusinessServices = Class.create();
BusinessServices.prototype = {
  initialize: function() {},
  type: 'BusinessServices',

  refreshBusinessServices: function() {
    var rest = new x_pd_integration.PagerDuty_REST();

    var fetchedItems = rest.getAllItems('business_services', transformItem);

    if (rest.hasError())
      return { error: rest.getError(), message: '' };

    if (!fetchedItems.length)
      return { error: '', message: 'No business services were fetched from PagerDuty' };

    var storedItems = getStoredItems('x_pd_integration_business_services', 'id');
    upsertBusinessServices(fetchedItems);
    var staleItems = subtract(storedItems, fetchedItems, 'id');
    inactivate('x_pd_integration_business_services', 'id', staleItems);

    return { error: '', message: 'Refreshed business services from PagerDuty' };
  }
};

var transformItem = function(item) {
  var subdomain = '';
  if (item.html_url) {
    subdomain = item.html_url.split('://').slice(-1)[0].split('.')[0];
  }
  return {
    active: true,
    id: item.id,
    name: item.name,
    subdomain: subdomain,
  };
};

var getStoredItems = function(tableName, fieldName) {
  var records = {};
  var rec = new GlideRecordSecure(tableName);
  rec.query();
  while (rec.next()) {
    if (rec[fieldName])
      records[rec[fieldName].toString()] = { active: rec.active.toString() };
  }
  return records;
};

var upsertBusinessServices = function(services) {
  services.forEach(function(ep) {
    var rec = new GlideRecordSecure('x_pd_integration_business_services_import');
    rec.initialize();
    rec.active = ep.active;
    rec.id = ep.id;
    rec.name = ep.name;
    rec.subdomain = ep.subdomain;
    rec.insert();
  });
};

var subtract = function(storedItems, fetchedItems, fieldName) {
  var staleItems = JSON.parse(JSON.stringify(storedItems));
  fetchedItems.forEach(function(fetchedObject) {
    if (staleItems[fetchedObject[fieldName]])
      delete staleItems[fetchedObject[fieldName]];
  });
  return staleItems;
};

var inactivate = function(tableName, fieldName, staleItems) {
  Object.keys(staleItems).forEach(function(id) {
    var rec = new GlideRecordSecure(tableName);
    rec.addQuery(fieldName, id);
    rec.query();
    while (rec.next()) {
      if (rec.active) {
        rec.active = false;
        rec.update();
      }
    }
  });
};
