sys_script_include.script:
/*** Changes made to this script are not supported by PagerDuty ***/
var PagerDutyCustomFieldsController = x_pd_integration.AjaxHelper.createAjaxController({
  type: 'PagerDutyCustomFieldsController',
  /* Sample Invocation Code:
   var customFieldsApi = x_pd_integration.AjaxHelper.ajaxClientFor('PagerDutyCustomFieldsController');
   customFieldsApi.createCustomFieldMapping({
      snFieldId: '359262a047bdb110b15271e7826d4354',
      pdFieldDisplayName: 'My Field_4',
      pdFieldName: 'Test_4',
      pdFieldDescription: 'My Field Description...',
    }, function (error, _result) {
      if (error) {
        jslog('Error creating custom field mapping: ' + error.message);
      }
    });
  */
  createCustomFieldMapping: function (mapping) {
    var enableCustomFieldMapping = gs.getProperty('x_pd_integration.enable_custom_field_mapping');
    if (enableCustomFieldMapping !== 'true') {
      gs.debug('PagerDuty create custom fields mapping is disabled, no ability present in the PD account');
      return;
    }

    var cfSupport = new x_pd_integration.PagerDutyCustomFieldsSupport();
    cfSupport.createMapping(mapping);
  },

  /**
   * Get all PagerDuty custom fields
   * @returns {Object} - return list of PagerDuty custom fields
   */
  getAllPagerDutyCustomFields: function () {
    var cfSupport = new x_pd_integration.PagerDutyCustomFieldsSupport();
    return cfSupport.getAllCustomFields();
  },

  /**
   * Get all custom fields mappings
   * @returns {Object} - return list of mappings
   */
  getAllCustomFieldsMappings: function () {
    var cfSupport = new x_pd_integration.PagerDutyCustomFieldsSupport();
    return cfSupport.getMappings(true);
  },

  /**
   * Get custom field mapping
   * @param {String} sysId - The sysId of the custom field mapping.
   * @returns {Object} - return mapping
   */
  getCustomFieldMapping: function (sysId) {
    var cfSupport = new x_pd_integration.PagerDutyCustomFieldsSupport();
    return cfSupport.getMappingBySysId(sysId);
  },

  /**
   * Check custom field mapping ability.
   * @returns {Boolean} - Whether the account has the custom field mapping ability.
   */
  checkCustomFieldMappingAbility: function () {
    var cfSupport = new x_pd_integration.PagerDutyCustomFieldsSupport();
    return cfSupport.checkCustomFieldMappingAbility();
  },

  type: 'PagerDutyCustomFieldsController'
});
