sys_script_include.script:
/*** Changes made to this script are not supported by PagerDuty ***/
var PagerDutySettingsController = Class.create();
PagerDutySettingsController.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

  getSystemProperties: function () {
    var props = {};
    var error = '';
    try {
      var propNames = this.getParameter('sysparm_prop_names');
      propNames = propNames.split(',');
      propNames.forEach(function (propName) {
        if (!propName) {
          return;
        }
        props[propName] = gs.getProperty('x_pd_integration.' + propName);
      });
    } catch (ex) {
      error = 'Failed to get the system property: ' + ex.message;
    }
    return JSON.stringify({ error: error, props: props });
  },

  type: 'PagerDutySettingsController',
});
