sys_script_include.script:
/*** Changes made to this script are not supported by PagerDuty ***/
var UiMacrosController = Class.create();
UiMacrosController.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

  getWebhookVersion: function () {
    var webhookId = this.getParameter('sysparm_webhook_id');
    var webhookVersion;
    var grPdWebhook = new GlideRecordSecure('x_pd_integration_pagerduty_webhooks');
    grPdWebhook.addQuery('webhook_id', webhookId);
    grPdWebhook.query();
    if (grPdWebhook.next()) {
      webhookVersion = grPdWebhook.getValue('webhook_version');
    }
    return webhookVersion;
  },

  type: 'UiMacrosController'
});
