sys_script_include.script:
/*** Changes made to this script are not supported by PagerDuty ***/
var TrackingController = x_pd_integration.AjaxHelper.createAjaxController({
  type: 'TrackingController',

  /**
   * Track user actions. Every action triggers an "event", which can also have associated properties.
   * @param {String} event - name of the action user performs
   * @param {String} properties - free-form dictionary of properties of the event
   */
  track: function (event, properties) {
    try {
      var pd = new x_pd_integration.TrackingService();
      pd.track(event, properties);
      gs.debug('TrackingController | Track Method | event: {0}', event);
    } catch (e) {
      gs.error('TrackingController | Track method | error: {0}', e);
    }
  },
});
