sys_ui_page.client_script:
var res = confirm('Are you sure to refresh PagerDuty business services?');

if (res)
	jQuery('form')[0].submit();
else
	window.history.back();

sys_ui_page.processing_script:
gs.info('Updating PagerDuty business services');
var message = '';
var pd = new x_pd_integration.BusinessServices();
var result = pd.refreshBusinessServices();

if (result.error) {
  message = 'PagerDuty business services error: ' + result.error;
  gs.error(message);
  gs.addErrorMessage(message);
}
else {
  message = 'PagerDuty business services successfully updated';
  gs.info(message);
  gs.addInfoMessage(message);
}

response.sendRedirect('x_pd_integration_business_services_list.do');
