sys_ui_page.client_script:
jQuery(function () {
  var $ = jQuery;
  var modal = GlideModal.get();
  var total = modal.getPreference('total');
  var ids = modal.getPreference('ids');

  $('#mappings-total').text(' ' + total + ' ');
  $('#total').val(total);
  $('#sysparm_sys_ids').val(ids);

  $('.pd-deactivate-multiple-mapping-form button.submit-request').on('click', function (event) {
    $('form')[0].submit();
    trackEvent();
  });

  $('.pd-deactivate-multiple-mapping-form button.submit-cancel').on('click', function (event) {
    event.preventDefault();
    event.stopPropagation();
    modal.destroy();
  });

  function trackEvent () {
    var trackingApi = x_pd_integration.AjaxHelper.ajaxClientFor('TrackingController');
    trackingApi.track('custom_fields_mapping_deactivate_multiple_button', {}, function (error, _result) {
      if (error) {
        jslog('Tracking error: ' + error);
      }
    });
  }
});

sys_ui_page.processing_script:
(function () {
  try {
    gs.debug('Deactivating field mapping(s): ' + this['sysparm_sys_ids']);
    var customFieldsSupport = new x_pd_integration.PagerDutyCustomFieldsSupport();
    customFieldsSupport.deactivateMappings(this['sysparm_sys_ids']);
    gs.addInfoMessage(this['total'] + ' field mapping(s) successfully deactivated.');
    response.sendRedirect('x_pd_integration_custom_field_mapping_list.do');
  } catch (e) {
    gs.error('Error deactivating field mapping(s): ' + e.message);
    gs.addErrorMessage('Could not deactivate field mapping(s). Please try again.');
    response.sendRedirect(gs.getSession().getUrlOnStack());
  }
})();
