sys_ui_page.client_script:
var res = confirm('Are you sure to refresh PagerDuty services?');

if (res)
	jQuery('form')[0].submit();
else
	window.history.back();

sys_ui_page.processing_script:
gs.info('Updating PagerDuty services');
var message = '';
var pd = new x_pd_integration.Services();
var result = pd.refreshServices();

if (result.error) {
  message = 'PagerDuty services error: ' + result.error;
  gs.error(message);
  gs.addErrorMessage(message);
}
else {
  message = 'PagerDuty services successfully updated';
  gs.info(message);
  gs.addInfoMessage(message);
}

response.sendRedirect('x_pd_integration_services_list.do');
