sys_ui_page.client_script:
var res = confirm('Are you sure to refresh PagerDuty priorities?');

if (res)
	jQuery('form')[0].submit();
else
	window.history.back();

sys_ui_page.processing_script:
gs.info('Updating PagerDuty priorities');
var message = '';
var pd = new x_pd_integration.PagerDutyPriorities();
var error = pd.updatePriorities();

if (error) {
  message = 'PagerDuty priorities error: ' + error;
  gs.error(message);
  gs.addErrorMessage(message);
}
else {
  message = 'PagerDuty priorities successfully updated';
  gs.info(message);
  gs.addInfoMessage(message);
}

response.sendRedirect('x_pd_integration_pagerduty_priority_list.do');
