sys_ui_script.script:
var x_pd_integration = x_pd_integration || {};

x_pd_integration.AjaxHelper = (function () {
  "use strict";

  function ajaxClientFor(controllerName) {
    return new Proxy({}, {
      get: function (target, name) {
        return function () {
          var args = [].slice.call(arguments);
          var callback = args.splice(-1)[0];

          var strArgs = JSON.stringify(args);
          var ga = new GlideAjax(controllerName);
          ga.addParam('sysparm_name', 'dispatch');
          ga.addParam('sysparm_json_name', name);
          ga.addParam('sysparm_json_argument', strArgs);
          ga.getXML(function (response) {
            var answer = JSON.parse(response.responseXML.documentElement.getAttribute("answer")) || {error: 'Failed to get response from Server'}
            callback(answer.error, answer.result);
          });
        };
      }
    });
  }

  return {
    ajaxClientFor: ajaxClientFor,
    type: "AjaxHelper"
  };
})();
