sysevent_script_action.script:
/*** Changes made to this script are not supported by PagerDuty ***/
/* This Script Action is invoked when processing 'x_pd_integration.process_webhook' events from a queue,
being it the default queue or a custom queue. */
var opts = JSON.parse(event.parm1);
var v3wh = new x_pd_integration.v3webhook();
if (v3wh.isSupported(opts.message)) {
  var inboundV3 = new x_pd_integration.PagerDutyInboundV3();
  inboundV3.consume(opts.message.event, opts.query_params);
} else {
  var PDInbound = new x_pd_integration.PagerDutyInbound();
  PDInbound.processRestWebhook(opts.message);
}
