sysevent_script_action.script:
/*** Changes made to this script are not supported by PagerDuty ***/
var createdBy = event.parm1;

gs.debug("PagerDuty event 'x_pd_integration.post_resolution_note' fired from {0} by {1}", current.getDisplayValue(), createdBy);
var pd = new x_pd_integration.PagerDuty();

if (createdBy != "system") {
	var resolutionNote = current.close_notes;
	if (resolutionNote !== "") {
		var note = "(from ServiceNow:" + createdBy + ") " + resolutionNote;
		pd.postIncidentNote(x_pd_integration.TaskEntityHelper.getPdId(current), note, createdBy, false);
	}
}
