sysevent_script_action.script:
/*** Changes made to this script are not supported by PagerDuty ***/
gs.debug('PagerDuty \'x_pd_integration.migrate_webhooks\' event fired from PagerDutyWebhooksMigrationController and start a PagerDuty Webhooks Migration Job with sys id {0}', current.sys_id.toString());
var webhooksSysIds = String(event.parm1);
var pd = new x_pd_integration.PagerDutyWebhooksMigration();
if (gs.nil(webhooksSysIds)) {
  gs.debug('No webhooks sys_ids provided, migrating all webhooks');
  pd.executeMigrationJob(current, []);
} else {
  webhooksSysIds = webhooksSysIds.split(',');
  pd.executeMigrationJob(current, webhooksSysIds);
}

// Update system property 'webhooks_v2_identified' after migration
var mng = new x_pd_integration.PagerDutyWebhooksManagement();
mng.setV2WebhooksIdentifiedProperty();
