sysevent_script_action.script:
/*** Changes made to this script are not supported by PagerDuty ***/
(function () {
  gs.debug('PagerDuty \'x_pd_integration.update_v2_wh_extension\' Webhook event fired from {0}', current.getDisplayValue());

  var webhookId = current.getValue('webhook_id');
  var extensionSchemaId = current.getValue('extension_schema_id');
  var taskType = current.getValue('task_type');

  if (gs.nil(extensionSchemaId)) {
    gs.error('The v2 webhook (extension) with the id [' + webhookId + '] ' +
      'was not updated in PagerDuty, because the extension schema id for this webhook ' +
      'is not populated in a table [' + current.getTableName() + ']. ' +
      'You can find the extension schema id via this API: ' +
      'https://developer.pagerduty.com/api-reference/93f606460579c-get-an-extension ' +
      'by appending ?include[]=extension_schemas to the request.');
    return;
  }

  // Naming decision explained here:
  // https://github.com/PagerDuty/integration-servicenow-app/pull/407#discussion_r1250857902
  var syncOption = current.getValue('sync_option') === 'manual' ? 'manual_sync' : 'sync_all';

  var requestBody = {
    extension: {
      extension_schema: {
        id: extensionSchemaId,
        type: 'extension_schema_reference'
      },
      config: {}
    }
  };

  if (taskType) {
    requestBody.extension.config.task_type = taskType;
  }
  if (syncOption) {
    requestBody.extension.config.sync_options = syncOption;
  }

  var pd = new x_pd_integration.PagerDuty();
  pd.updatePdExtension(webhookId, requestBody);

})();
